import UIKit

protocol ClaimResultBusinessLogic {
    func doSomething(request: ClaimResult.Something.Request)
}

protocol ClaimResultDataStore {
    //var name: String { get set }
}

class ClaimResultInteractor: ClaimResultBusinessLogic, ClaimResultDataStore {
    var presenter: ClaimResultPresentationLogic?
    var worker: ClaimResultWorker?
    //var name: String = ""

    // MARK: Do something

    func doSomething(request: ClaimResult.Something.Request) {
        worker = ClaimResultWorker()
        worker?.doSomeWork()

        let response = ClaimResult.Something.Response()
        presenter?.presentSomething(response: response)
    }
}
