import UIKit
import RxCocoa
import RxSwift

protocol ClaimResultDisplayLogic: class {
    func displaySomething(viewModel: ClaimResult.Something.ViewModel)
}

class ClaimResultViewController: UIViewController, ClaimResultDisplayLogic {
    var interactor: ClaimResultBusinessLogic?
    var router: (NSObjectProtocol & ClaimResultRoutingLogic & ClaimResultDataPassing)?

    // MARK: Object lifecycle

    override init(nibName nibNameOrNil: String?, bundle nibBundleOrNil: Bundle?) {
        super.init(nibName: nibNameOrNil, bundle: nibBundleOrNil)
        setup()
    }

    required init?(coder aDecoder: NSCoder) {
        super.init(coder: aDecoder)
        setup()
    }

    // MARK: Setup

    private func setup() {
        let viewController = self
        let interactor = ClaimResultInteractor()
        let presenter = ClaimResultPresenter()
        let router = ClaimResultRouter()
        viewController.interactor = interactor
        viewController.router = router
        interactor.presenter = presenter
        presenter.viewController = viewController
        router.viewController = viewController
        router.dataStore = interactor
    }

    // MARK: Routing

    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        if let scene = segue.identifier {
            let selector = NSSelectorFromString("routeTo\(scene)WithSegue:")
            if let router = router, router.responds(to: selector) {
                router.perform(selector, with: segue)
            }
        }
    }

    // MARK: View lifecycle

    override func viewDidLoad() {
        super.viewDidLoad()
//        doSomething()

        setRX()
    }

    private let disposeBag = DisposeBag()

    @IBOutlet weak var backBtn: UIButton!
    @IBOutlet weak var bottomBtn: UIButton!

    // MARK: Do something

    //@IBOutlet weak var nameTextField: UITextField!

    func doSomething() {
        let request = ClaimResult.Something.Request()
        interactor?.doSomething(request: request)
    }

    func displaySomething(viewModel: ClaimResult.Something.ViewModel) {
        //nameTextField.text = viewModel.name
    }
}

extension ClaimResultViewController {

    func setRX() {

        self.backBtn.rx.tap
            .subscribe(onNext: {
                self.navigationController?.popViewController(animated: true)
            }).disposed(by: disposeBag)

        self.bottomBtn.rx.tap
            .subscribe(onNext: {
                guard let viewControllers = self.navigationController?.viewControllers else { return }
                self.navigationController?.popToViewController(viewControllers[1], animated: true)
            }).disposed(by: disposeBag)
    }
}
